import React , {useEffect}  from 'react'
import {useState} from 'react'
import {Card} from '@material-ui/core'
import {useDispatch,useSelector} from 'react-redux'
import {loadUserAction} from '../../store/actions/User/ProfileActions'
import {useStyles} from '../../styles'
import Skeleton from '@material-ui/lab/Skeleton';
import ContactCard from './ContactCard'
import ConnectionCard from './ConnectionCard'
import HttpService from '../../services/HttpService'

export default function ProfileView() {

    const dispatch = useDispatch();
    const classes = useStyles();
    const userProfile = useSelector(state => state.userProfile.userProfile);

    useEffect(() => {
        dispatch(loadUserAction());
    }, [dispatch])

    const [contactList, updateContactList] = useState(
        {
            users:[],
            success: false
        }
    )

    const [connectionList, updateConnectionList] = useState(
        {
            connections:[],
            success: false
        }
    )

    function getAllUsers(){
        if (contactList.success === false){
            const http = new HttpService();
            let registered_users = "user/get-registered-users";
            const tokenId = "user-token";

            return http.getData(registered_users,tokenId).then(data=>{
                console.log(JSON.stringify(data));
                updateContactList(
                    {
                        users: data.users,
                        success: true  
                    }
                )
                return data;
            }).catch((error)=> {
                console.log(error)
                return error; 
                });
        }
    }

    function getAllConnections(){
        const http = new HttpService();
        let get_connections_url = "user/get-connected-users"
        const tokenId = "user-token"
        let body = JSON.stringify({ email: userProfile.user.email })
        console.log(body)
        // return http.postData(body ,get_connections_url,tokenId).then(data=>{
        //     console.log(JSON.stringify(data));
        //     updateConnectionList(
        //         {
        //             connections: data.result,
        //             success: true
        //         }
        //     )
        //     return data;
        // }).catch((error)=> {
        //     console.log(error)
        //     return error; 
        //     });
    }

    // getAllUsers()
    // getAllConnections()

    let contacts = contactList.users.map(item => <ContactCard user={item} thisUser = {userProfile} updateConnections = {updateConnectionList} />)
    let connections = connectionList.connections.map(item => <ConnectionCard connection = {item}/>)

    return (
        <>
            <div className={classes.fullWidthProfile}>
            <Card>
                {
                    userProfile!="" && userProfile.success==true?
                            <div>
                            <h3><b>Name: </b>{userProfile.user.name}</h3>
                            <h3><b>Email: </b>{userProfile.user.email}</h3>
                            <h3><b>Account Creation Date: </b>{userProfile.user.created_at}</h3>
                            </div>
                    :
                    userProfile.success==false ?
                    userProfile.message
                    :
                    <Skeleton variant="rect" width={210} height={118} />

                }
            </Card>
            {getAllUsers()}
            {getAllConnections()}
            <br></br>
            {
                contactList.success?
                    <div>
                    {contacts}
                    </div>
                :
                    <div>
                    <h1>Loading...</h1>
                    </div>
            }
                        <br></br>
            {
                connectionList.success?
                    <div>
                    {connections}
                    </div>
                :
                    <div>
                    <h1>Loading...</h1>
                    </div>
            }
            </div>
        </>
    )
}
